import React from "react";
import { useParams } from "react-router-dom";
import { UseSelector, useSelector } from "react-redux";

const WordDetailsPage = ()=>{
    const {word} = useParams();
    const wordData = useSelector((state)=>state.wordData);

    // Find the word data based on the selected word
    // const selectedWorddata = wordData.find((data) => data.word === word);
    const selectedWorddata = wordData.filter((data) => data.word === word)[0];
    if (!selectedWorddata) {
        return <div>
            Word not found
        </div>;
    }

    return (
        <div>
            <h1>Word Details for {selectedWorddata.word}</h1>
            <p>Phonetic: {}selectedWorddata.phonetic</p>
            <h2>Phonetics</h2>
            <ul>
                {selectedWorddata.phonetics.map((phonetic, index) => (
                    <li key={index}>
                        {phonetic.text} - {' '}
                        <audio controls>
                            <source src={phonetic.audio} type="audio/mpeg"/>
                            Your browser does not support the audio element.
                        </audio>
                    </li>
                ))}
            </ul>

            <h2>Meanings</h2>
             {selectedWorddata.meanings.map((meaning, index) => (
            <div key={index}>
                 <h3>Part of Speech: {meaning.partOfSpeech}</h3>
                <ul>
                 {meaning.definitions.map((definition, index) => (
                     <li key={index}>
                     <p>Definition: {definition.definition}</p>
                    {definition.synonyms && definition.synonyms.length > 0 && (
                      <p>Synonyms: {definition.synonyms.join(', ')}</p>
                      )}
                     {definition.antonyms && definition.antonyms.length > 0 && (
                     <p>Antonyms: {definition.antonyms.join(', ')}</p>
                     )}
                     {definition.example && (
                      <p>Example: {definition.example}</p>
                    )}
                  </li>
                ))}
            </ul>
        </div>
      ))}
    </div>
    )
}

export default WordDetailsPage;
