import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {AddToHistory}  from '../ReduxFile/actions/HistoryActions';
import  {fetchWordData} from '../ReduxFile/actions/WordActions';
// import WordDetailsPage from './WorddetailsPage';


const HomePage = ()=>{
    const [searchWord, setSearchWord] = useState('');
    const dispatch = useDispatch();
    // const wordData = useSelector((state)=>state.wordData);

    const handleSearch = () => {
        if(searchWord.trim() !== ''){
            dispatch(fetchWordData(searchWord));
            dispatch(AddToHistory(searchWord));
            setSearchWord("");
        }
    };

    return (
        <div>
            <input 
                type='text'
            placeholder='Enter a word'
            value={searchWord}
            onChange={(e) => setSearchWord(e.target.value)}
            />
            <button
                onClick={handleSearch}
            >search</button>

            {searchWord && searchWord.length > 0 && (
                <div>
                    <h2>Search Results For "{searchWord}"</h2> 
                    {searchWord.map((wordInfo, index)=> (
                        <div key={index}>
                            <h3>{wordInfo.word}</h3>
                            <p>Phonetic: {wordInfo.phonetic}</p>
                            </div>
                    ))}
                </div>
            )}
            
        </div>
    );
};

export default HomePage;
