import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import {AddToHistory}  from '../ReduxFile/actions/HistoryActions';
import  {fetchWordData} from '../ReduxFile/actions/WordActions';
import WordDetailsPage from './WorddetailsPage';


const HomePage = ()=>{
    const [searchWord, setSearchWord] = useState('');
    const dispatch = useDispatch();

    const handleSearch = () => {
        if(searchWord.trim() !== ''){
            dispatch(fetchWordData(searchWord));
            dispatch(AddToHistory(searchWord));
            setSearchWord(searchWord);
        }
    };

    return (
        <div>
            <input 
                type='text'
            placeholder='Enter a word'
            value={searchWord}
            onChange={(e) => setSearchWord(e.target.value)}
            />
            <button
                onClick={handleSearch}
            >search</button>

            {searchWord && (
                <h2>Search Results</h2>
                
            )}
            <WordDetailsPage/>
        </div>
    );
};

export default HomePage;
