import React, { useState, useEffect } from 'react';

const HomePage = () => {
  const [searchWord, setSearchWord] = useState('');
  const [wordData, setWordData] = useState(null);

  const handleSearch = async () => {
    if (searchWord.trim() !== '') {
      try {
        const response = await fetch(`https://api.dictionaryapi.dev/api/v2/entries/en/${searchWord}`);
        if (response.ok) {
          const data = await response.json();
          setWordData(data[0]); // Assuming the API response is an array with one object
        } else {
          setWordData(null);
        }
      } catch (error) {
        console.error('Error fetching word data:', error);
        setWordData(null);
      }
    }
  };

  useEffect(() => {
    if (wordData) {
      console.log('Word data:', wordData); // You can log the data for further inspection
    }
  }, [wordData]);

  return (
    <div>
      <h1>Dictionary App</h1>
      <input
        type="text"
        placeholder="Enter a word"
        value={searchWord}
        onChange={(e) => setSearchWord(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {wordData && (
        <div>
          <h2>Search Results for "{searchWord}"</h2>
          <p>Word: {wordData.word}</p>
          <p>Phonetic: {wordData.phonetic}</p>
          
          {wordData.meanings.map((meaning, index) => (
            <div key={index}>
              <h3>Part of Speech: {meaning.partOfSpeech}</h3>
              <ul>
                {meaning.definitions.map((definition, index) => (
                  <li key={index}>
                    <p>Definition: {definition.definition}</p>
                    {definition.synonyms && definition.synonyms.length > 0 && (
                      <p>Synonyms: {definition.synonyms.join(', ')}</p>
                    )}
                    {definition.antonyms && definition.antonyms.length > 0 && (
                      <p>Antonyms: {definition.antonyms.join(', ')}</p>
                    )}
                    {definition.example && (
                      <p>Example: {definition.example}</p>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          ))}

          {wordData.phonetics.map((phonetic, index) => (
            <div key={index}>
              <p>Phonetic Text: {phonetic.text}</p>
              <audio controls>
                <source src={phonetic.audio} type="audio/mpeg" />
                Your browser does not support the audio element.
              </audio>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HomePage;
